/**
 * Class YubiKey_Authenticator
 * @package YubiKey
 *
 * Authenticates a user with username and password with the
 * YubiKey services.
 * The password should be a One Time Pad generated by a YubiKey
 * password generator.
 */
class YubiKey_Authenticator {

  private static $id = "19628";
  private static $key = "u0W17Of4vqfpKnjZi3BXhA9H6jQ=";

  /**
   * Tries to authenticate a user with username and password (which should, in
   * this case, e
   *
   * @param $username
   * @param $password
   * @return null|User
   */
  public static function authenticate($username, $password) {

    YubiKey_Logger::log("Authenticating User ".$username);

    $pimcore_user = User::getByName($username);
    if (! $pimcore_user instanceof User) {
      YubiKey_Logger::log("User ".$username." nicht gefunden.");
      return null;
    }

    $yubikey_user = YubiKey_User::getById($pimcore_user->getId());

    if (is_null($yubikey_user)) return null;

    if (!$yubikey_user->getActivelocal()) {
      return null;
    }

    $yubico = new Auth_Yubico(self::$id, self::$key);

    $serial = substr($password, 0, 12);

    foreach ($yubikey_user->getKeys() as $key) {
      if ($key["serial"] == $serial) {
        try {
          $yubico->verify($password);
        } catch (Exception $e) {
          YubiKey_Logger::log("Authentication failed: " . $e->getMessage());
          YubiKey_Logger::debug("Debug output from server: ".$yubico->getLastResponse());
          return null;
        }

        YubiKey_Logger::log("Success Authenticating User ".$username);
        return $pimcore_user;
      }
    }
    return null;
  }
}